mapper.select:
select * from free_board_comment
where free_board_idx=#{free_board_idx}
order by step asc

select * from free_board_comment
where member_idx=#{member_idx}
order by step asc

mapper.update:
update free_board_comment set
comment=#{comment}, post=#{post}
where free_board_comment_idx=#{free_board_comment_idx}
