mapper.select:
	select count(*) from (
		select * from interest_exr_routine where member_idx = #{member_idx}
		 union
		select * from interest_exr_tip where member_idx = #{member_idx}
 		 union
		select * from interest_diet_share where member_idx = #{member_idx}
 		 union
 		select * from interest_diet_tip where member_idx = #{member_idx}
	) test

		SELECT *  FROM (
			SELECT ROW_NUMBER() OVER (ORDER BY regdate DESC) AS ROWNUM, table_name, idx, title, writer, regdate FROM (
				select '운동루틴' as table_name, exr_routine_idx as idx, title, writer, regdate from exr_routine where member_idx = #{member.member_idx}
				union
				select '운동팁' as table_name, exr_tip_idx as idx, title, writer, regdate from exr_tip where member_idx = #{member.member_idx}
				union
				select '식단공유' as table_name, diet_share_idx as idx,  title, writer, regdate from diet_share where member_idx = #{member.member_idx}
 				union
 				select '식단팁' as table_name, diet_tip_idx as idx, title, writer, regdate from diet_tip where member_idx = #{member.member_idx}
			) result
		)  interest WHERE ROWNUM BETWEEN #{start} AND #{end};
